import React from 'react';
import '../styles.css'

function Header(){

    // OnLoad animation
    window.onload = function(){
        const header = document.querySelector('header');
        const headerTxt = document.querySelector('.header-txt');
        const headerBold = document.querySelector('.header-bold');
        const headerBtn = document.querySelector('.header-btn');
        const headerTxtAnim = headerTxt.animate([
            {opacity: 0, transform: 'translateY(50px)'},
            {opacity: 1, transform: 'translateY(0px)'}
        ],{
            duration: 1000,
            fill: 'forwards',
            easing: 'ease-in-out'
        });
        const headerBoldAnim = headerBold.animate([
            {opacity: 0, transform: 'translateY(50px)'},
            {opacity: 1, transform: 'translateY(0px)'}
        ],{
            duration: 1000,
            fill: 'forwards',
            easing: 'ease-in-out',
            delay: 500
        });
        const headerBtnAnim = headerBtn.animate([
            {opacity: 0, transform: 'translateY(50px)'},
            {opacity: 1, transform: 'translateY(0px)'}
        ],{
            duration: 1000,
            fill: 'forwards',
            easing: 'ease-in-out',
            delay: 1000
        });
    }


    return(
        <header style={styles.header}>
            <p className="main-txt font-reg" style={styles.headerTxt}>Heyo, I'm Jaegar</p>
            <h1 className="main-txt" style={styles.headerBold}>
                My passion is <span class="clr-text">building</span> and <br></br><span class="clr-text">designing</span> for the web.
            </h1>
            <a href="#about" className="header-btn">Learn More</a>
        </header>
    )
}
const styles={
    header:{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
    },
    headerTxt:{
        textAlign: 'center',
        fontSize: '1.5rem',
    },
    headerBold:{
        textAlign: 'center',
        fontSize: '3.75rem',
    }
}
export default Header;
