import React from 'react';
import '../styles.css'

function Header(){

    window.onload = function(){
        const header = document.querySelector('.header');

        // Animate the header from being really zoomed in to normal size
        header.style.transform = 'scale(10)';
        header.style.opacity = '1';
        header.style.transition = 'all 1s ease-in-out';

    }





    return(
        <header style={styles.header} className='header'>
            <p className="main-txt font-reg header-txt" style={styles.headerTxt} >Heyo, I'm Jaegar</p>
            <h1 className="main-txt header-bold" style={styles.headerBold}>
                My passion is <span class="clr-text">building</span> and <br></br><span class="clr-text">designing</span> for the web.
            </h1>
            <a href="#about" className="header-btn">Learn More</a>
        </header>
    )
}
const styles={
    header:{
        width: '100%',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'column',
    },
    headerTxt:{
        textAlign: 'center',
        fontSize: '1.5rem',
    },
    headerBold:{
        textAlign: 'center',
        fontSize: '3.75rem',
    }
}
export default Header;
