import React from "react";
import "../styles.css";

function Badge(props) {
    return (
        <div style={styles.badge}>
            <p style={styles.txt} className="font-reg txt-secondary">{props.txt}</p>
        </div>
    )
}

const styles = {
    badge: {
        backgroundColor: 'var(--badge-clr)',
        borderRadius: '5px',
        padding: '5px 10px',
        margin: '5px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',


    },
    txt: {
        fontSize: '.79rem',
    }
}

export default Badge;
