import React from "react";
import "../styles.css";
import Badge from "./Badge";

function ProjectCard(props) {
    return (
        <div style={styles.card}>
            <div style={styles.imgWrapper}>


            </div>
            <div style={styles.txtCont}>
                <h1 style={styles.title} className="font-reg main-txt">{props.title}</h1>
                <p style={styles.desc} className="font-reg txt-secondary">{props.desc}</p>
            </div>
            <div style={styles.badgeCont}>
                <Badge txt={props.badge1} />
            </div>
        </div>
    )
}

const styles = {
    card: {
        width: '350px',
        height: '400px',
        border: '1px solid var(--primary-clr)',
        borderRadius: '10px',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',

    },
    imgWrapper: {
        width: '100%',
        height: 'auto',
        borderRadius: '10px',
        padding: '10px',
        border: '1px solid var(--faded-text-clr)',
    },
    txtCont: {
        width: '100%',
        height: 'auto',
        padding: '10px',
    },
    title: {
        fontSize: '1.25rem',
        marginBottom: '10px',
    },
    desc: {
        fontSize: '1rem',
    },
    badgeCont: {
        width: '100%',
        height: 'auto',
        padding: '10px',
    }
}

export default ProjectCard;
